import React, { useState } from 'react'; 
import { Link,  } from 'react-router-dom';

function HomePage() {
    const [boxes, setBoxes] = useState([]);
    
    const addBox = async () => 
    {
      const newBox = 
      {
        id: boxes.length + 1,
      };

      const backend = await fetch('http://localhost:8000/api/tables/', 
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newBox)
      });
      setBoxes([...boxes, newBox]);
    };

    const deleteBox = () =>
    {
      setBoxes(boxes.slice(0, boxes.length - 1));
    };
    
    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      {/* Zeige username nur an, wenn er vorhanden ist */}
      <h1 className='text-center text-5xl mt-10 mb-5'></h1>
      <div className='flex justify-between'>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            Men√º
        </button>
      
        <div>
            <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              register
            </Link>
            <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              log in
            </Link>
        </div>
      </div>
      <div>
        <div className='flex flex-wrap justify-center gap-4'>
          {boxes.map((box) => 
          (
            <div className="w-32 h-32 border-4 border-black flex justify-center items-center" key={box.id}>{box.id}</div>
          ))} 
        </div>
        <div className='flex justify-center mt-4' onClick={addBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            add table
          </button>
        </div>
            
        <div className='flex justify-center mt-4' onClick={deleteBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            delete table
          </button>
        </div>
      </div>

    </div>
    );
}

export default HomePage;
