import React, { useState, useEffect } from 'react'; 
import { useParams  } from 'react-router-dom';

function Category() {
const { categoryName } = useParams();
const [menuItems, setMenuItems] = useState([]);
const [name, setName] = useState('');
  const [price, setPrice] = useState('');

  return (
    <div className='min-h-screen bg-gray-100'>
    <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
    <div className='flex flex-wrap justify-center'>
      <div className='border-2 border-gray-700 p-6 m-4 text-center bg-white shadow-lg'>
        {categoryName}
      </div>
    </div>
    <form className='text-center flex justify-center flex-col w-80'>
      <input
        type="text"
        placeholder="name"
        required
      />
      <input
        type="number"
        placeholder="price"
        required
      />
      <button type="submit" className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700' >
        add MenuItem
      </button>
    </form>
    <div className='text-center mt-5'>
        {menuItems.map(menuItem => (
            <div className="mx-auto mb-4 p-4 bg-blue-100 rounded-lg shadow-md cursor-pointer hover:bg-blue-200 transition-colors duration-200 ease-in-out"
                onClick={() => navigateCategoryPage(menuItem.id, menuItem.name, menuItem.price, menuItem.number)}
                key={menuItem.id}>
                {menuItem.name}
                {menuItem.price}
                {menuItem.number}
            </div>
        ))}
    </div>
  </div>
  );
}

export default Category;
