import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function LogIn() {
    const[username, setUsername] = useState('');
    const[password, setPassword] = useState('');
    const history = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('https//localhost:8000/api/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({username, password}),
            });
            if (response.ok) {
                const {token} = await response.json();
                // Speichere das Token im lokalen Speicher oder in Cookies
                localStorage.setItem('token', token);
                // Leite den Benutzer auf die Hauptseite oder das Dashboard um
                navigate('/');
                console.log("Login successful");
            } else {
                console.error('failed')
            }
        } catch (error){
            console.error("Network problem", error);
        }
    };

    return(
        <div className='min-h-screen bg-gray-100'>
        <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
        
        <h2 className="text-2xl font-bold mb-4">Login</h2>

        <form onSubmit={handleSubmit} className="space-y-6">
            <div className="form-group">
            <input autofocus className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="text" name="username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username"></input>
            </div>
            <div className="form-group">
            <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="password" value={password} onChange={(e) => setUsername(e.target.value)} name="password" placeholder="Password"></input>
            </div>
            <input className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit" value="Login"></input>
        </form>

        <div className="mt-4">
            Don't have an account? <a href="/Register" class="text-blue-500 hover:text-blue-800">Register here.</a>
        </div>

      </div>
    );
}

export default LogIn;
