import React, { useState, useEffect } from 'react'; 
import { Link, useNavigate } from 'react-router-dom';

function HomePage() {
  const [username, setUsername] = useState(localStorage.getItem('username') || ''); 
  const [tableNumbers, setTableNumbers] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('token') ? true : false);
  const navigate = useNavigate(); // Hinzufügen des useNavigate Hooks
  
  // Beim Laden der HomePage-Komponente, um vorhandene Tische zu laden
// Beim Laden der HomePage-Komponente, um vorhandene Tische zu laden
useEffect(() => {
  fetch('http://localhost:8000/api/tables/', {
    headers: {
      'Authorization': `Token ${localStorage.getItem('token')}`, // Füge das Token hinzu
    },
  })
  .then(response => response.json())
  .then(data => setTableNumbers(data.map(table => table.number)))
  .catch(error => console.error('Fehler beim Laden der Tische:', error));
}, []);

// Funktion, um einen neuen Tisch hinzuzufügen
const addTable = () => {
  const newTableNumber = tableNumbers.length + 1; // Dies könnte ein Problem sein, wenn Tische gelöscht werden. Überlege eine robustere Methode zur Nummernvergabe.
  fetch('http://localhost:8000/api/tables/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Token ${localStorage.getItem('token')}`, // Füge das Token hinzu
    },
    body: JSON.stringify({ number: newTableNumber }),
  })
  .then(response => {
    if (response.ok) {
      // Es ist besser, die aktuelle Liste der Tische neu zu laden oder die Antwort vom Server zu verwenden
      return response.json();
    } else {
      throw new Error('Fehler beim Hinzufügen des Tisches');
    }
  })
  .then(response => response.ok ? response.json() : Promise.reject('Fehler beim Hinzufügen des Tisches'))
  .then(data => setTableNumbers(prev => [...prev, data.number]))
  .catch(error => console.error(error));
};

const logout = () => {
  localStorage.removeItem('token');
  localStorage.removeItem('username');
  setIsLoggedIn(false);
  setUsername('');
  navigate('/login'); // Leitet den Benutzer zur Login-Seite um
};



    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      {/* Zeige username nur an, wenn er vorhanden ist */}
      {isLoggedIn && username && <h1 className='text-center text-5xl mt-10 mb-5'>{username}</h1>}
      <div className='flex justify-between'>
      <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
          Menü
      </button>
      <div>
      {!isLoggedIn ? (
      <>
          <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            register
          </Link>
          <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            log in
          </Link>
        </>
      ) : (
        <button onClick={logout} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
          Logout
        </button>
    )}
  </div>
      
      </div>
      
      <div className='flex flex-wrap justify-center'>
      {tableNumbers.map((tableNumber) => (
          <Link to={`/order/${tableNumber}`} key={index}>
            <div className='border-2 border-gray-700 p-6 m-4 text-center bg-white shadow-lg'>
              Tisch {tableNumber}
            </div>
          </Link>
        ))}
      </div>
      <div className='text-center mt-5'>
        <button onClick={addTable} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
          Tisch hinzufügen
        </button>
      </div>

    </div>
    );
}

export default HomePage;
