import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function OrderPage() {
  const { boxNumber } = useParams(); 
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/categories/', {
        method: 'GET',
        headers: {
          'Authorization': `Token ${token}`,
        },
      });
  
      if (response.ok) {
        const fetchedCategories = await response.json();
        setCategories(fetchedCategories);
      } else {
        console.error('Fehler beim Laden der Kategorien');
      }
    };
  
    fetchCategories();
  }, []);
  // Logik zum Laden der MenuItems für die ausgewählte Kategorie

  useEffect(() => {
    if (selectedCategoryId) {
      fetchMenuItems(selectedCategoryId); // Rufe die Funktion auf, die MenuItems basierend auf der categoryId abruft
    }
  }, [selectedCategoryId]);

  const fetchMenuItems = async (categoryId) => {
    const token = localStorage.getItem('token');
    const response = await fetch(`http://localhost:8000/api/menuItems/?categoryId=${categoryId}`, {
      method: 'GET',
      headers: {
        'Authorization': `Token ${token}`,
      },
    });
  
    if (response.ok) {
      const data = await response.json();
      setMenuItems(data);
    } else {
      console.error('Fehler beim Laden der MenuItems');
    }
  };
  
    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <div className='flex flex-wrap justify-center'>
        {/* Box für Tisch Nr. 1 */}
        <div className='border-2 border-gray-700 p-6 m-4 text-center bg-white shadow-lg'>
          Tisch {boxNumber}
        </div>
        {/* Weitere Boxen würden hier hinzugefügt */}
      </div>
      <select 
        value={selectedCategoryId || ''}
        onChange={e => setSelectedCategoryId(e.target.value)}
      >
        <option value="">Wähle eine Kategorie</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>
            {category.name}
          </option>
        ))}
      </select>
      <div>
        {menuItems.map((menuItem, index) => (
          <div key={index}>
            <h3>{menuItem.name}</h3>
            <p>Preis: {menuItem.price}</p>
            {/* Weitere Informationen oder Aktionen für jedes MenuItem */}
          </div>
        ))}
      </div>

      <div className=''>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
         + hinzufügen
        </button>
      </div>
    </div>
    );
}

export default OrderPage;
