import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function OrderPage() {
  const { boxId, boxNumber } = useParams(); // Angenommen, du kannst boxId aus den Params erhalten
  const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
      const fetchMenuItems = async () => {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8000/api/menuItems/?categoryId=${categoryId}`, 
        {
          method: 'GET',
          headers: {
            'Authorization': `Token ${token}`, // Angenommen, der Token wird im Local Storage gespeichert
          },
        });
    
        if (response.ok) {
          const data = await response.json();
          setMenuItems(data);
        } else {
          console.error('Fehler beim Laden der Kategorien');
        }
      };
    
      fetchMenuItems();
    }, [categoryId]);

    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <div className='flex flex-wrap justify-center'>
        {/* Box f端r Tisch Nr. 1 */}
        <div className='border-2 border-gray-700 p-6 m-4 text-center bg-white shadow-lg'>
          Tisch {boxNumber}
        </div>
        {/* Weitere Boxen w端rden hier hinzugef端gt */}
      </div>
      <div className=''>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
         + hinzuf端gen
        </button>
      </div>
    </div>
    );
}

export default OrderPage;
