import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function OrderPage() {
  const { boxId, boxNumber } = useParams(); 
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [addedMenuItems, setAddedMenuItems] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/categories/', {
        method: 'GET',
        headers: {
          'Authorization': `Token ${token}`,
        },
      });
  
      if (response.ok) {
        const fetchedCategories = await response.json();
        setCategories(fetchedCategories);
      } else {
        console.error('Fehler beim Laden der Kategorien');
      }
    };
  
    fetchCategories();
  }, []);

  useEffect(() => {
    const fetchAddedMenuItems = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8000/api/tables/${boxId}/menu_items/`, {
        method: 'GET',
        headers: {
          'Authorization': `Token ${token}`,
        },
      });
  
      if (response.ok) {
        const data = await response.json();
        setAddedMenuItems(data);  // Aktualisiere den Zustand mit den abgerufenen Daten
      } else {
        console.error('Fehler beim Laden der hinzugefügten Menüpunkte');
      }
    };
  
    fetchAddedMenuItems();
  }, [boxId]); 

  // Logik zum Laden der MenuItems für die ausgewählte Kategorie

  useEffect(() => {
    if (selectedCategoryId) {
      fetchMenuItems(selectedCategoryId); // Rufe die Funktion auf, die MenuItems basierend auf der categoryId abruft
    }
  }, [selectedCategoryId]);

  const fetchMenuItems = async (categoryId) => {
    const token = localStorage.getItem('token');
    const response = await fetch(`http://localhost:8000/api/menuItems/?categoryId=${categoryId}`, {
      method: 'GET',
      headers: {
        'Authorization': `Token ${token}`,
      },
    });
  
    if (response.ok) {
      const data = await response.json();
      setMenuItems(data);
    } else {
      console.error('Fehler beim Laden der MenuItems');
    }
  };

  const addMenuItemToTable = async (menuItemId) => {
    const menuItem = menuItems.find(item => item.id === menuItemId);
    if (menuItem) {
      const updatedAddedItems = new Map(addedMenuItems);
      const currentQuantity = updatedAddedItems.get(menuItemId) || 0;
      updatedAddedItems.set(menuItemId, currentQuantity + 1);
      setAddedMenuItems(updatedAddedItems);
    } 
    const token = localStorage.getItem('token'); // Sicherstellen, dass du den korrekten Authentifizierungsmechanismus anwendest
    const response = await fetch(`http://localhost:8000/api/tables/${boxId}/addMenuItem`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${token}`,
      },
      body: JSON.stringify({ menuItemId }),
    });
  
    if (response.ok) {
      // Erfolg: Feedback an Benutzer oder aktualisiere den State, um die Änderung widerzuspiegeln
      console.log("MenuItem erfolgreich zum Tisch hinzugefügt.");
    } else {
      // Fehler: Feedback an Benutzer geben
      console.error("Fehler beim Hinzufügen des MenuItems zum Tisch.");
    }
  };
  
  const handleCategoryChange = (e) => {
    setSelectedCategoryId(e.target.value);
  };
  
    return (
      <div className='min-h-screen bg-gray-100 py-10'>
      <h1 className='text-center text-5xl mb-10'>ServeSavvy - Tisch {boxNumber}</h1>

      <div className='flex justify-center mb-10'>
        <select
          className="p-2 border-2 border-gray-200 rounded"
          value={selectedCategoryId}
          onChange={handleCategoryChange}
        >
          <option value="">Wähle eine Kategorie</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </div>

      <div className='flex justify-between'>
        <div className='w-1/2 pr-2'>
          {/* Menüpunkte zur Auswahl */}
          {menuItems.map((menuItem) => (
            <div className="mb-4 p-4 bg-blue-100 rounded-lg shadow-md transition-colors duration-200 ease-in-out"
                key={menuItem.id}>
              <h3 className="text-xl font-semibold mb-2">{menuItem.name}</h3>
              <p className="text-lg mb-2">{menuItem.price} €</p>
              <button 
                onClick={() => addMenuItemToTable(menuItem.id)} 
                className='mt-2 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700'
              >
                Zu Tisch {boxNumber} hinzufügen
              </button>
            </div>
          ))}
        </div>
        <div className='w-1/2 pl-2'>
          {/* Angezeigte hinzugefügte Menüpunkte */}
          <h2 className='text-xl font-bold mb-4'>Hinzugefügte Speisen:</h2>
          {addedMenuItems.map((menuItem, index) => (
            <div className="mb-4 p-4 bg-green-100 rounded-lg shadow-md" key={index}>
              <p className="text-lg">{menuItem.name} - {menuItem.price} €</p>
            </div>
          ))}
        </div>
      </div>
      
      <div className='w-1/2 pl-2'>
        <h2 className='text-xl font-bold mb-4'>Hinzugefügte Speisen:</h2>
        {Array.from(addedMenuItems.keys()).map((menuItemId, index) => {
          const menuItem = menuItems.find(item => item.id === menuItemId);
          const quantity = addedMenuItems.get(menuItemId);
          return (
            <div className="mb-4 p-4 bg-green-100 rounded-lg shadow-md" key={index}>
              <p className="text-lg">{menuItem.name} - {menuItem.price} € - Anzahl: {quantity}</p>
            </div>
          );
        })}
      </div>
      
    </div>
    );
}

export default OrderPage;
