import React, { useState, useEffect } from 'react'; 
import { Link, useNavigate } from 'react-router-dom';

function HomePage() {
    const [boxes, setBoxes] = useState([]);
    const [userId, setUserId] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
  // Funktion zum Abrufen der Boxen vom Server
  const fetchBoxes = async (userId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('Kein Token gefunden, Benutzer ist wahrscheinlich nicht eingeloggt');
      return;
    }

    try {
      const response = await fetch('http://localhost:8000/api/tables/', {
        headers: {
          'Authorization': `Token ${token}`
        },
      });
      if (response.ok) {
        const fetchedBoxes = await response.json();
        // Stelle sicher, dass nur Boxen für den aktuellen Benutzer gesetzt werden
        const userBoxes = fetchedBoxes.filter(box => box.user === userId);
        setBoxes(userBoxes);
      } else {
        // Fehlerbehandlung, z.B. wenn die Antwort nicht ok ist
        console.error('Fehler beim Laden der Boxen');
      }
    } catch (error) {
      // Fehlerbehandlung, z.B. bei Netzwerkfehlern
      console.error('Fehler beim Abrufen der Boxen:', error);
    }
  };

  // Benutzer-ID aus dem localStorage abrufen
  const userIdFromStorage = localStorage.getItem('userId');
  setUserId(userIdFromStorage);

  // Boxen für den aktuellen Benutzer abrufen, wenn eine userId vorhanden ist
  if (userIdFromStorage) {
    fetchBoxes(userIdFromStorage);
  } else {
    console.log('Keine userId im localStorage gefunden');
  }
}, []); // Die leeren Abhängigkeiten bedeuten, dass dieser Effekt nur beim Laden der Komponente ausgeführt wird


    const logout = () => {
      localStorage.removeItem('userId');
      console.log('removed');
      navigate('/login'); 
  };
    
    const addBox = async () => 
    {
      const token = localStorage.getItem('token');

      const backend = await fetch('http://localhost:8000/api/tables/', 
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Token ${token}`
        },
        body: JSON.stringify({})
      });
      if(backend.ok)
      {
        const newBox = await backend.json();
        setBoxes([...boxes, newBox]);
      }
    };

    const deleteBox = async () =>
    {
      const backend = await fetch('http://localhost:8000/api/tables/delete',
      {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (backend.ok)
      {
        setBoxes(boxes.slice(0, boxes.length - 1));
      }
    };
    
    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <div className='flex justify-between'>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            Menü
        </button>
      
        <div>
            <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              register
            </Link>
            <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              log in
            </Link>
        </div>
        {userId && (
                        <button onClick={logout} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
                            Logout
                        </button>
                    )}
      </div>
      <div>
        <div className='flex flex-wrap justify-center gap-4'>
          {boxes.map((box) => 
          (
            <div className="w-32 h-32 border-4 border-black flex justify-center items-center" key={box.id}>{box.id}</div>
          ))} 
        </div>
        <div className='flex justify-center mt-4' onClick={addBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            add table
          </button>
        </div>
            
        <div className='flex justify-center mt-4' onClick={deleteBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            delete table
          </button>
        </div>
      </div>

    </div>
    );
}

export default HomePage;
