import React, { useState, useEffect } from 'react'; 
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';

function HomePage() {
  // Beim Laden der HomePage-Komponente, um vorhandene Tische zu laden
useEffect(() => {
  fetch('/api/tables/') // Achte darauf, die richtige URL anzugeben
    .then(response => response.json())
    .then(data => setTableNumbers(data.map(table => table.number)))
    .catch(error => console.error('Fehler beim Laden der Tische:', error));
}, []);

// Funktion, um einen neuen Tisch hinzuzufügen
const addTable = () => {
  const newTableNumber = tableNumbers.length + 1;
  fetch('/api/tables/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ number: newTableNumber }),
  })
    .then(response => {
      if (response.ok) {
        setTableNumbers([...tableNumbers, newTableNumber]);
      } else {
        console.error('Fehler beim Hinzufügen des Tisches');
      }
    })
    .catch(error => console.error('Fehler:', error));
};


    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <h1 className='text-center text-5xl mt-10 mb-5'>{username}</h1>
      <div className='flex justify-between'>
      <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
          Menü
      </button>
      <div>
      <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
        register
      </Link>
      <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
        log in
      </Link>
      </div>
      
      </div>
      
      <div className='flex flex-wrap justify-center'>
        {/* Map over tableNumbers to create links */}
        {tableNumbers.map((tableNumber) => (
          <Link to={`/order/${tableNumber}`} key={tableNumber}>
            <div className='border-2 border-gray-700 p-6 m-4 text-center bg-white shadow-lg'>
              Tisch {tableNumber}
            </div>
          </Link>
        ))}
      </div>
      <div className='text-center mt-5'>
        <button onClick={addTable} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
          Tisch hinzufügen
        </button>
      </div>

    </div>
    );
}

export default HomePage;
