import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Link } from 'react-router-dom';


function Register() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmation, setConfirmation] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (password !== confirmation) {
            console.error("no match");
            return;
        }
        try {
            const response = await fetch('http://localhost:8000/api/register/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, email, password}),                
            });
            if (response.ok) {
                history.push('/'); // go to that url
                console.log("worked");
            }
            else
            {
                console.error("failed");
            }
        }
        catch (error) {
            console.error("Network problem", error);
        }
    };
    return(
        <div className='min-h-screen bg-gray-100'>
        <h2 className="text-2xl font-bold mb-4">Register</h2>


    <form onSubmit={handleSubmit} className="space-y-6">
        <div className="form-group">
            <input autoFocus className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="text" name="username" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username"></input>
        </div>
        <div className="form-group">
            <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email Address"></input>
        </div>
        <div className="form-group">
            <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password"></input>
        </div>
        <div className="form-group">
            <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" type="password" name="confirmation" value={confirmation} onChange={(e) => setConfirmation(e.target.value)} placeholder="Confirm Password"></input>
        </div>
        <input className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit" value="Register"></input>
    </form>

    <div className="mt-4">
        Already have an account? <Link to="/login" className="text-blue-500 hover:text-blue-800">Log In here.</Link>

    </div>

    </div>

    );
}

export default Register;
