import React, { useState, useEffect } from 'react'; 
import { Link, useNavigate } from 'react-router-dom';

function HomePage() {
    const [boxes, setBoxes] = useState([]);
    const [userId, setUserId] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
      // Diese Funktion wird definiert, um die Tabellen abzurufen
      const fetchUserTables = async () => {
        const token = localStorage.getItem('token'); // Token aus dem Local Storage holen
        if (!token) {
          console.log('Kein Token gefunden');
          return;
        }
            const response = await fetch('http://localhost:8000/api/tables/', {
              method: 'GET',
              headers: {
                'Authorization': `Token ${token}`,
              },
            });
      
            if (response.ok) {
              const tables = await response.json();
              setBoxes(tables); // Aktualisiere den Zustand mit den erhaltenen Tabellen
            } else {
              // Fehlerbehandlung
              console.error('Fehler beim Laden der Tabellen');
            }
       
      };
    
      // Die Funktion wird aufgerufen, um die Tabellen zu laden.
      fetchUserTables();
    }, []); // Leeres Abhängigkeitsarray, um die Funktion nur beim Mounten der Komponente auszuführen
    
    

    const logout = () => {
      localStorage.removeItem('userId');
      console.log('removed');
      navigate('/login'); 
  };
    
    const addBox = async () => 
    {
      const token = localStorage.getItem('token');

      const backend = await fetch('http://localhost:8000/api/tables/', 
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Token ${token}`
        },
        body: JSON.stringify({})
      });
      if(backend.ok)
      {
        const newBox = await backend.json();
        setBoxes([...boxes, newBox]);
      }
    };

    const deleteBox = async () =>
    {
      const backend = await fetch('http://localhost:8000/api/tables/delete',
      {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (backend.ok)
      {
        setBoxes(boxes.slice(0, boxes.length - 1));
      }
    };
    
    return (
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <div className='flex justify-between'>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            Menü
        </button>
      
        <div>
            <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              register
            </Link>
            <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              log in
            </Link>
        </div>
        {userId && (
                        <button onClick={logout} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
                            Logout
                        </button>
                    )}
      </div>
      <div>
        <div className='flex flex-wrap justify-center gap-4'>
          {boxes.map((box, index) => (
            <div className="w-32 h-32 border-4 border-black flex justify-center items-center" key={box.id}>{`Box ${index + 1}`}</div>
          ))}
        </div>
        <div className='flex justify-center mt-4' onClick={addBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            add table
          </button>
        </div>
            
        <div className='flex justify-center mt-4' onClick={deleteBox}>
          <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            delete table
          </button>
        </div>
      </div>

    </div>
    );
}

export default HomePage;
