import React from 'react';

function Menu() {
    const[categories, setCategories] = useState([]);
    const [newCategoryName, setNewCategoryName] = useState('');

    useEffect(() => {
      const fetchCategories = async () => {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:8000/api/tables/', {
          method: 'GET',
          headers: {
            'Authorization': `Token ${token}`, // Angenommen, der Token wird im Local Storage gespeichert
          },
        });
  
        if (response.ok) {
          const data = await response.json();
          setCategories(data);
        } else {
          console.error('Fehler beim Laden der Kategorien');
        }
      };
  
      fetchCategories();
    }, []);

    const addCategory = async (e) => {
      e.preventDefault();
      setIsLoading(true);
  
      const response = await fetch('/api/categories/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ name: newCategoryName }),
      });
  
      if (response.ok) {
        const newCategory = await response.json();
        setCategories([...categories, newCategory]); // Füge die neue Kategorie zur Liste hinzu
        setNewCategoryName(''); // Setze das Formular zurück
      } else {
        console.error('Fehler beim Hinzufügen der Kategorie');
      }
    };
    return(
    <div className='min-h-screen bg-gray-100'>
      <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
      <h1 className='text-center text-5xl mt-10 mb-5'>Menü</h1>
      <div className='text-center mt-5'>
        <button className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
         + add category
        </button>
      </div>
    </div>
    );
}

export default Menu;
