import React, { useState, useEffect } from 'react'; 
import { Link, useNavigate } from 'react-router-dom';

function HomePage() {
    const [boxes, setBoxes] = useState([]);
    const [userId, setUserId] = useState(null);
    const navigate = useNavigate();
  
    useEffect(() => {
      //display the tables
      const fetchUserTables = async () => {
        const token = localStorage.getItem('token'); // get token of local storage
        const backend = await fetch('http://localhost:8000/api/tables/', 
        {
          method: 'GET',
          headers: {
            'Authorization': `Token ${token}`,
          },
        });
  
        if (backend.ok) {
          let tables = await backend.json();
          setBoxes(tables); // update the tables
        } else {
          console.error('Fehler beim Laden der Tabellen');
        }
       
      }
      const userIdFromStorage = localStorage.getItem('userId');
      setUserId(userIdFromStorage); 

      fetchUserTables();
    }, []); 
    
    const navigateOrderPage = (boxId, boxNumber) => 
    {
      navigate(`/OrderPage/${boxId}/${boxNumber}`)
    }

    const logout = () => {
      localStorage.removeItem('userId');
      console.log('removed userId');
      localStorage.removeItem('token');
      console.log('removed token');
      navigate('/login'); 
    };
    
    const addBox = async () => 
    {
      const token = localStorage.getItem('token');

      const backend = await fetch('http://localhost:8000/api/tables/', 
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Token ${token}`
        },
        body: JSON.stringify({})
      });
      if(backend.ok)
      {
        const newBox = await backend.json();
        setBoxes([...boxes, newBox]);
      }
    };

    const deleteBox = async () => 
    {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8000/api/tables/', {
        method: 'DELETE',
        headers: {
          'Authorization': `Token ${token}`, 
        },
      });
  
      if (response.ok) {
        setBoxes(prevBoxes => prevBoxes.slice(0, -1));
      } else {
        alert('couldnt delete table.');
      }
    };
    
    
    return (
      <div className='min-h-screen bg-gray-100'>
        <h1 className='text-center text-5xl mt-10 mb-5'>ServeSavvy</h1>
        <div className='flex justify-between'>
          <Link to={`/menu`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
            Menü
          </Link>
          <div>
            {!userId && (
              <Link to={`/register`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
                Register
              </Link>
            )}
            {!userId && (
              <Link to={`/login`} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
                Log In
              </Link>
            )}
          </div>
          {userId && (
            <button onClick={logout} className='bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              Logout
            </button>
          )}
        </div>
    
        <div>
          <div className='flex flex-wrap justify-center gap-4'>
            {boxes.map((box, index) => {
              const isActive = localStorage.getItem(`table_${box.id}_hasItems`) === 'true';
              return (
                <div 
                  className={`w-32 h-32 border-4 ${isActive ? 'bg-red-500' : 'border-black'} flex justify-center items-center`} 
                  onClick={() => navigateOrderPage(box.id, index + 1)} 
                  key={box.id}
                >
                  {`Tisch ${index + 1}`}
                </div>
              );
            })}
          </div>
    
          <div className='flex justify-center mt-4' onClick={addBox}>
            <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              Tisch hinzufügen
            </button>
          </div>
              
          <div className='flex justify-center mt-4' onClick={deleteBox}>
            <button className='text-center bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700'>
              Tisch löschen
            </button>
          </div>
        </div>
      </div>
    );    
}

export default HomePage;
